def classify_institution(name):
    name = str(name).lower()

    if "univ" in name or "college" in name:
        return "University"
    elif "museum" in name:
        return "Museum"
    elif "historical" in name or "history center" in name or "heritage" in name or "society" in name:
        return "Historical Center"
    elif "national park" in name or "np" in name or "nm" in name or "nra" in name or "nmp" in name:
        return "National Park / Monument"
    elif "forest" in name or "fs" in name or "nf" in name:
        return "National Forest"
    elif "nps" in name or "blm" in name or "fws" in name or "army" in name or "doe" in name or "us dept" in name:
        return "Federal Agency"
    elif "state dept" in name or "department of" in name:
        return "State Agency"
    elif "library" in name:
        return "Library"
    elif "coroner" in name or "medical examiner" in name or "sheriff" in name or "forensic" in name:
        return "Forensic / Law"
    elif "school" in name:
        return "School / District"
    else:
        return "Other"

df_inventories['Institution_Type'] = df_inventories['Institution'].apply(classify_institution)
df_notices['Institution_Type'] = df_notices['Institution'].apply(classify_institution)
df_summaries['Institution_Type'] = df_summaries['Institution'].apply(classify_institution)

